{
  "actions": [
    {
      "title": "Find Products",
      "description": "Search for products using filters like name, SKU, visibility status, and modified date range. Returns an array of matching product objects. Note: Pagination parameters (page, offset, limit) are omitted.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/catalog/products/getproducts",
      "endpointUrl": "/v3/catalog/products",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "GET",
      "sampleResponse": {
        "data": [
          {
            "id": 123,
            "name": "Sample Product",
            "sku": "SP-001",
            "price": "19.99",
            "is_visible": true
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Search string to filter products by name or SKU."
          },
          "is_visible": {
            "type": "boolean",
            "description": "Filter products based on their visibility status."
          },
          "date_modified_start": {
            "type": "string",
            "description": "Filter products modified after this ISO8601 date."
          },
          "date_modified_end": {
            "type": "string",
            "description": "Filter products modified before this ISO8601 date."
          }
        }
      },
      "outputType": {
        "outputType": "array",
        "arrayPropertyValue": "data"
      }
    },
    {
      "title": "Get Product",
      "description": "Retrieve a single product by its unique product ID.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/catalog/products/getproductbyid",
      "endpointUrl": "/v3/catalog/products/{product_id}",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "GET",
      "sampleResponse": {
        "id": 123,
        "name": "Sample Product",
        "sku": "SP-001",
        "price": "19.99",
        "is_visible": true
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "product_id": {
            "type": "number",
            "description": "The unique identifier of the product."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create Product",
      "description": "Create a new product with specified fields such as name, SKU, price, and description.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/catalog/products/createproduct",
      "endpointUrl": "/v3/catalog/products",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "POST",
      "sampleResponse": {
        "data": {
          "id": 124,
          "name": "New Product",
          "sku": "NP-001"
        }
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "Name of the product."
          },
          "type": {
            "type": "string",
            "description": "Type of product (physical/digital)."
          },
          "price": {
            "type": "number",
            "description": "Price of the product."
          },
          "sku": {
            "type": "string",
            "description": "Stock Keeping Unit value."
          },
          "weight": {
            "type": "number",
            "description": "Weight of the product (if applicable)."
          },
          "categories": {
            "type": "array",
            "description": "List of category IDs for the product.",
            "items": {
              "type": "number"
            }
          },
          "description": {
            "type": "string",
            "description": "Description of the product."
          },
          "is_visible": {
            "type": "boolean",
            "description": "Visibility status of the product."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update Product",
      "description": "Update an existing product using its product ID. Accepts fields such as name, price, and visibility to update.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/catalog/products/updateproduct",
      "endpointUrl": "/v3/catalog/products/{product_id}",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "PUT",
      "sampleResponse": {},
      "inputSchema": {
        "type": "object",
        "properties": {
          "product_id": {
            "type": "number",
            "description": "The unique identifier of the product to update."
          },
          "name": {
            "type": "string",
            "description": "Updated name of the product."
          },
          "price": {
            "type": "number",
            "description": "Updated price of the product."
          },
          "is_visible": {
            "type": "boolean",
            "description": "Updated visibility status."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete Product",
      "description": "Delete a product identified by its product ID.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/catalog/products/deleteproduct",
      "endpointUrl": "/v3/catalog/products/{product_id}",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "DELETE",
      "sampleResponse": {},
      "inputSchema": {
        "type": "object",
        "properties": {
          "product_id": {
            "type": "number",
            "description": "The unique identifier of the product to delete."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Find Customers",
      "description": "Search for customers using filters such as email, name, or date created. Returns an array of matching customer objects.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/customers-v3/customers/getcustomers",
      "endpointUrl": "/v3/customers",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "GET",
      "sampleResponse": {
        "data": [
          {
            "id": 201,
            "email": "customer@example.com",
            "first_name": "John",
            "last_name": "Doe"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Search string to filter customers by name or email."
          },
          "date_created_start": {
            "type": "string",
            "description": "Filter customers created after this ISO8601 date."
          },
          "date_created_end": {
            "type": "string",
            "description": "Filter customers created before this ISO8601 date."
          }
        }
      },
      "outputType": {
        "outputType": "array",
        "arrayPropertyValue": "data"
      }
    },
    {
      "title": "Get Customer",
      "description": "Retrieve details of a single customer using their unique customer ID.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/customers-v3/customers/getacustomer",
      "endpointUrl": "/v3/customers/{customer_id}",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "GET",
      "sampleResponse": {
        "id": 201,
        "email": "customer@example.com",
        "first_name": "John",
        "last_name": "Doe"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "customer_id": {
            "type": "number",
            "description": "The unique identifier of the customer."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create Customer",
      "description": "Create a new customer by providing essential details such as email, first name, last name, and phone. Optionally include addresses and company information.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/customers-v3/customers/createcustomers",
      "endpointUrl": "/v3/customers",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "POST",
      "sampleResponse": {
        "data": {
          "id": 202,
          "email": "newcustomer@example.com"
        }
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "email": {
            "type": "string",
            "description": "The customer's email address."
          },
          "first_name": {
            "type": "string",
            "description": "The customer's first name."
          },
          "last_name": {
            "type": "string",
            "description": "The customer's last name."
          },
          "phone": {
            "type": "string",
            "description": "The customer's phone number."
          },
          "addresses": {
            "type": "array",
            "description": "List of addresses associated with the customer.",
            "items": {
              "type": "object",
              "properties": {
                "street_1": {
                  "type": "string",
                  "description": "Primary street address."
                },
                "city": {
                  "type": "string",
                  "description": "City name."
                },
                "state": {
                  "type": "string",
                  "description": "State or province."
                },
                "zip": {
                  "type": "string",
                  "description": "Postal code."
                },
                "country": {
                  "type": "string",
                  "description": "Country name."
                }
              }
            }
          },
          "company": {
            "type": "string",
            "description": "Company name (if applicable)."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update Customer",
      "description": "Update an existing customer's details using their customer ID. Fields such as email, first name, last name, and phone can be updated.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/customers-v3/customers/updateacustomer",
      "endpointUrl": "/v3/customers/{customer_id}",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "PUT",
      "sampleResponse": {},
      "inputSchema": {
        "type": "object",
        "properties": {
          "customer_id": {
            "type": "number",
            "description": "The unique identifier of the customer to update."
          },
          "email": {
            "type": "string",
            "description": "Updated email address."
          },
          "first_name": {
            "type": "string",
            "description": "Updated first name."
          },
          "last_name": {
            "type": "string",
            "description": "Updated last name."
          },
          "phone": {
            "type": "string",
            "description": "Updated phone number."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Find Orders",
      "description": "Search for orders using filters such as status, date range, customer ID, or email. Returns an array of matching order objects. Note: Pagination parameters are omitted.",
      "type": "action",
      "documentation": "https://developer.bigcommerce.com/api-reference/store-management/orders/orders/getorders",
      "endpointUrl": "/v2/orders",
      "apiBaseUrl": "https://api.bigcommerce.com/stores/{store_hash}/",
      "method": "GET",
      "sampleResponse": {
        "data": [
          {
            "id": 301,
            "status_id": 1,
            "total_inc_tax": "59.99"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Search string to filter orders by customer email or other attributes."
          },
          "status_id": {
            "type": "number",
            "description": "Filter orders by a specific status identifier."
          },
          "min_date_created": {
            "type": "string",
            "description": "ISO8601 date to filter orders created after this date."
          },
          "max_date_created": {
            "type": "string",
            "description": "ISO8601 date to filter orders created before this date."
          }
        }
      },
      "outputType": {
        "outputType": "array",
        "arrayPropertyValue": "data"
      }
    }
  ],
  "authentication": {
    "type": "oauth2",
    "authUrl": "https://login.bigcommerce.com/oauth2/authorize",
    "tokenUrl": "https://login.bigcommerce.com/oauth2/token",
    "profileInfoUrl": "https://api.bigcommerce.com/stores/{store_hash}/v3/customers/me"
  },
  "serviceJson": {
    "name": "appmixer.bigcommerce",
    "label": "BigCommerce",
    "category": "applications",
    "description": "Integration component for managing BigCommerce products, customers, and orders using OAuth2 authentication.",
    "version": "1.0.0",
    "icon": "https://cdn.example.com/icons/bigcommerce.svg"
  }
}

