{
  "actions": [
    {
      "title": "Find Users",
      "description": "Retrieve a list of users with optional search criteria. This endpoint allows filtering via a query parameter.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "GET",
      "sampleResponse": {
        "users": [
          {
            "id": "user_123",
            "email": "alice@example.com",
            "firstName": "Alice",
            "lastName": "Doe"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for searching users",
        "properties": {
          "query": {
            "type": "string",
            "description": "Search query for filtering users by name or email."
          }
        }
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get User",
      "description": "Retrieve details of a specific user identified by user ID.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "GET",
      "sampleResponse": {
        "id": "user_123",
        "email": "alice@example.com",
        "firstName": "Alice",
        "lastName": "Doe"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for retrieving a specific user",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the user."
          }
        },
        "required": ["id"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Create User",
      "description": "Create a new user with the provided information.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "POST",
      "sampleResponse": {
        "id": "user_124",
        "email": "bob@example.com",
        "firstName": "Bob",
        "lastName": "Smith"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a new user",
        "properties": {
          "email": {
            "type": "string",
            "description": "Email address of the new user."
          },
          "firstName": {
            "type": "string",
            "description": "First name of the new user."
          },
          "lastName": {
            "type": "string",
            "description": "Last name of the new user."
          },
          "password": {
            "type": "string",
            "description": "Password for the new user."
          }
        },
        "required": ["email", "firstName", "lastName", "password"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Update User",
      "description": "Update existing user details such as email, name or metadata.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "PATCH",
      "sampleResponse": {
        "id": "user_123",
        "email": "alice_new@example.com",
        "firstName": "Alice",
        "lastName": "Doe"
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for updating a user",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the user."
          },
          "email": {
            "type": "string",
            "description": "Updated email address."
          },
          "firstName": {
            "type": "string",
            "description": "Updated first name."
          },
          "lastName": {
            "type": "string",
            "description": "Updated last name."
          }
        },
        "required": ["id"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Delete User",
      "description": "Remove a user from the system based on the provided user ID.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "DELETE",
      "sampleResponse": {
        "success": true,
        "message": "User deleted successfully."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for deleting a user",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the user to be deleted."
          }
        },
        "required": ["id"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Ban User",
      "description": "Ban a user by revoking all active sessions and disabling access.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users/{id}/ban",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "POST",
      "sampleResponse": {
        "id": "user_123",
        "banned": true,
        "message": "User has been banned."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for banning a user",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the user to be banned."
          },
          "reason": {
            "type": "string",
            "description": "Optional reason for banning the user."
          }
        },
        "required": ["id"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Unban User",
      "description": "Remove the ban from a previously banned user, restoring access.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#users",
      "endpointUrl": "/users/{id}/unban",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "POST",
      "sampleResponse": {
        "id": "user_123",
        "banned": false,
        "message": "User has been unbanned."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for unbanning a user",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the user to be unbanned."
          }
        },
        "required": ["id"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
