      "inputSchema": {
        "type": "object",
        "description": "Input parameters for creating a new organization",
        "properties": {
          "name": {
            "type": "string",
            "description": "Name of the organization."
          },
          "metadata": {
            "type": "object",
            "description": "Additional metadata for the organization."
          }
        },
        "required": ["name"]
