    {
      "title": "Delete Organization",
      "description": "Delete an organization by its identifier.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#organizations",
      "endpointUrl": "/organizations/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "DELETE",
      "sampleResponse": {
        "success": true,
        "message": "Organization deleted successfully."
