    {
      "title": "Add User to Organization",
      "description": "Add a user to a specific organization.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#organizations",
      "endpointUrl": "/organizations/{id}/users",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "POST",
      "sampleResponse": {
        "organizationId": "org_001",
        "userId": "user_123",
        "message": "User added to organization."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for adding a user to an organization",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the organization."
          },
          "userId": {
            "type": "string",
            "description": "Unique identifier of the user to add."
          }
        },
        "required": ["id", "userId"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Remove User from Organization",
      "description": "Remove a user from a specific organization.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#organizations",
      "endpointUrl": "/organizations/{id}/remove_user",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "POST",
      "sampleResponse": {
        "organizationId": "org_001",
        "userId": "user_123",
        "message": "User removed from organization."
      },
      "inputSchema": {
        "type": "object",
        "description": "Input parameters for removing a user from an organization",
        "properties": {
          "id": {
            "type": "string",
            "description": "Unique identifier of the organization."
          },
          "userId": {
            "type": "string",
            "description": "Unique identifier of the user to remove."
          }
        },
        "required": ["id", "userId"]
      },
      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Find Sessions",
      "description": "Retrieve a list of active sessions with optional search criteria.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#sessions",
      "endpointUrl": "/sessions",
      "apiBaseUrl": "https://api.clerk.com/v1",
