      "outputType": {
        "outputType": "object"
      }
    },
    {
      "title": "Get Session",
      "description": "Retrieve details of a specific session using its unique identifier.",
      "type": "action",
      "documentation": "https://clerk.com/docs/references/backend/overview#sessions",
      "endpointUrl": "/sessions/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "GET",
