      "documentation": "https://clerk.com/docs/references/backend/overview#emails",
      "endpointUrl": "/emails/{id}",
      "apiBaseUrl": "https://api.clerk.com/v1",
      "method": "DELETE",
      "sampleResponse": {
        "success": true,
        "message": "Email deleted successfully."
