{
  "actions": [
    {
      "title": "Get Email",
      "description": "Retrieve details of a specific email by its ID. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#get-email",
      "endpointUrl": "https://api.resend.com/emails/{id}",
      "apiBaseUrl": "https://api.resend.com",
      "method": "GET",
      "sampleResponse": {
        "id": "email_123",
        "status": "sent",
        "to": "user@example.com",
        "subject": "Hello World"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "description": "The unique identifier of the email to retrieve."
          }
        },
        "required": [
          "id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Find Emails",
      "description": "Retrieve a list of sent emails with optional filtering. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#get-emails",
      "endpointUrl": "https://api.resend.com/emails",
      "apiBaseUrl": "https://api.resend.com",
      "method": "GET",
      "sampleResponse": {
        "emails": [
          {
            "id": "email_123",
            "status": "sent",
            "to": "user@example.com"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Query parameter for searching emails."
          }
        }
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": "emails"
      }
    },
    {
      "title": "Create Domain",
      "description": "Add a new domain for sending emails. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#create-domain",
      "endpointUrl": "https://api.resend.com/domains",
      "apiBaseUrl": "https://api.resend.com",
      "method": "POST",
      "sampleResponse": {
        "id": "domain_123",
        "domain": "example.com",
        "status": "pending"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "domain": {
            "type": "string",
            "description": "The domain name to add for email sending."
          }
        },
        "required": [
          "domain"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Get Domain",
      "description": "Retrieve information about a specific domain by its ID. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#get-domain",
      "endpointUrl": "https://api.resend.com/domains/{id}",
      "apiBaseUrl": "https://api.resend.com",
      "method": "GET",
      "sampleResponse": {
        "id": "domain_123",
        "domain": "example.com",
        "status": "verified"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "description": "The unique identifier of the domain."
          }
        },
        "required": [
          "id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": ""
      }
    },
    {
      "title": "Find Domains",
      "description": "Retrieve a list of verified domains with optional filtering. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#get-domains",
      "endpointUrl": "https://api.resend.com/domains",
      "apiBaseUrl": "https://api.resend.com",
      "method": "GET",
      "sampleResponse": {
        "domains": [
          {
            "id": "domain_123",
            "domain": "example.com",
            "status": "verified"
          }
        ]
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Query parameter for searching domains."
          }
        }
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": "domains"
      }
    },
    {
      "title": "Verify Domain",
      "description": "Trigger the domain verification process to confirm ownership. Requires an API Key in the Authorization header as a Bearer token.",
      "type": "action",
      "documentation": "https://resend.com/docs/api-reference#verify-domain",
      "endpointUrl": "https://api.resend.com/domains/{id}/verify",
      "apiBaseUrl": "https://api.resend.com",
      "method": "POST",
      "sampleResponse": {
        "id": "domain_123",
        "domain": "example.com",
        "status": "verified"
      },
      "inputSchema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "description": "The unique identifier of the domain to verify."
          }
        },
        "required": [
          "id"
        ]
      },
      "outputType": {
        "outputType": "object",
        "arrayPropertyValue": ""
      }
    }
  ],
  "authentication": {
    "type": "apiKey",
    "authUrl": "https://resend.com/dashboard",
    "tokenUrl": "https://resend.com/api/tokens",
    "profileInfoUrl": "https://api.resend.com/account"
  },
  "serviceJson": {
    "name": "appmixer.resend",
    "label": "Resend",
    "category": "applications",
    "description": "Resend is an email API service that provides transactional email delivery, domain management, and API key operations. It is designed for developers to easily integrate reliable email functionalities into their applications.",
    "version": "1.0.0",
    "icon": "https://resend.com/icon.svg"
  }
}
